["custom"] = "-- Keystone item IDs\naura_env.ids = {\n    [138019] = true, -- Legion Dungeons\n    [158923] = true, -- BFA Dungeons\n    [180653] = true, -- Shadowlands & Dragonflight Dungeons\n    [187786] = true, -- Legion Timewalking Dungeons\n}\n\n-- Custom options\naura_env.options = {'party', 'guild', 'spam'}\naura_env.types = {}\n\nfor _, v in ipairs(aura_env.options) do\n    if aura_env.config['enabled'..v] then\n        aura_env.types[v] = true\n    end\nend\n\n-- Force update on init\naura_env.key = nil\n\n-- Search the bags for the item IDs from the table above\naura_env.update = function()\n    aura_env.keys = {}\n    \n    for bag = 0, NUM_BAG_SLOTS do\n        local bagSlots = C_Container.GetContainerNumSlots(bag)\n        for slot = 1, bagSlots do\n            local info = C_Container.GetContainerItemInfo(bag, slot)\n            if info then\n                local itemID, hyperlink = info.itemID, info.hyperlink\n                if aura_env.ids[itemID] then\n                    aura_env.keys[itemID] = hyperlink\n                end\n            end\n        end\n    end\nend\n\n-- Send message to channel\naura_env.link = function(channel)\n    -- No key found, force update\n    if not aura_env.keys then aura_env.update() end\n    \n    -- Key found - link it in chat\n    if aura_env.keys then\n        aura_env.message = ''\n        \n        for _, link in next, aura_env.keys do\n            aura_env.message = (aura_env.message ~= '' and (aura_env.message..' & ') or '')..link\n        end\n        \n        if aura_env.message ~= '' then\n            SendChatMessage(aura_env.message, channel)\n        end\n    end\nend\n\ndo -- Anti-Spam check\n    local spamTimes = { GUILD = 0, PARTY = 0 }\n    aura_env.isSpam = function(channel)\n        local current = GetTime()\n        if current > spamTimes[channel] then\n            spamTimes[channel] = current + 30 -- Next allowed time in seconds\n            return false\n        else\n            return true -- Its still spam\n        end\n    end\nend",
