["init"] = {
["custom"] = "C_ChatInfo.RegisterAddonMessagePrefix(\"MYTHICAUTOMARK\")\naura_env.rc = LibStub(\"LibRangeCheck-3.0\")\nlocal config = aura_env.config.settings\naura_env.mobs = {}\naura_env.queue = {}\naura_env.marks = {\n    [1] = { [\"active\"] = false },\n    [2] = { [\"active\"] = false },\n    [3] = { [\"active\"] = false },\n    [4] = { [\"active\"] = false },\n    [5] = { [\"active\"] = false },\n    [6] = { [\"active\"] = false },\n    [7] = { [\"active\"] = false },\n    [8] = { [\"active\"] = false },\n}\n\naura_env.CheckModifierState = function()\n    -- Returns a BOOLEAN if the modifier state criteria is met, depending on what the player has set the setting to be. \n    local modifierKeySetting = config.mouseover.modifier\n    if modifierKeySetting == 1 then\n        return true\n    else\n        local modifierKeys = {\n            [2] = IsShiftKeyDown(),\n            [3] = IsControlKeyDown(),\n            [4] = IsAltKeyDown(),\n        }\n        return modifierKeys[modifierKeySetting]\n    end\nend\n\naura_env.CheckMarkDistance = function(npcId, unit, guid)\n    -- Returns a BOOLEAN if the mark distance criteria is met. \n    local range = aura_env.rc:GetRange(unit)\n    local distance = aura_env.mobs[npcId].distance == 10 and config.global.markDistance or aura_env.mobs[npcId].distance\n    if distance == 1 then \n        for i = 1, 40, 1 do \n            if guid == UnitGUID(\"nameplate\"..i) then return true end\n        end\n        return false\n    elseif distance == 2 then\n        return true\n    else\n        return range <= (distance+1)*10\n    end\nend\n\naura_env.GetInstanceID = function()\n    -- Returns a STRING with the Instance ID the player curently is in. \n    return tostring(select(8, GetInstanceInfo())) or \"\" \nend\n\naura_env.GetChatType = function()\n    -- Returns a STRING with the Chat Type for 'SendAddonMessage'.\n    return IsInGroup(LE_PARTY_CATEGORY_INSTANCE) and \"INSTANCE_CHAT\" or \"PARTY\"\nend\n\naura_env.GetPlayerName = function()\n    -- Returns a STRING with the correct player name format.\n    local name, realm = UnitFullName(\"player\")\n    if realm then \n        return string.format(\"%s-%s\", name, realm)\n    else\n        return nil\n    end\nend\n\naura_env.GetNpcIdFromGUID = function(guid)\n    -- Returns an INT with npcId from GUID\n    if not guid then return end\n\n    local npcId = select(6, strsplit(\"-\", guid))\n    return tonumber(npcId)\nend\n\naura_env.GetUnitIdFromGUID = function(guid)\n    -- Returns STRING with UnitId or nil\n    for i = 1, 40 do\n        if UnitGUID(\"nameplate\"..i) == guid then\n            return \"nameplate\"..i\n        end\n    end\n    return nil\nend\n\naura_env.PlayerWantsToMark = function()\n    local role = UnitGroupRolesAssigned(\"player\") == \"NONE\" and select(5, GetSpecializationInfo(GetSpecialization())) or UnitGroupRolesAssigned(\"player\")\n    local wantsToMark = config.markerInfo.role == 1 or\n                        (config.markerInfo.role == 2 and role == \"TANK\") or \n                        (config.markerInfo.role == 3 and UnitIsGroupLeader(\"player\")) or \n                        (config.markerInfo.role == 4 and (UnitIsGroupLeader(\"player\") or role == \"TANK\"))\n    return wantsToMark\nend\n\naura_env.partyMembers = {\n    [aura_env.GetPlayerName()] = {\n        [\"role\"] = UnitGroupRolesAssigned(\"player\") == \"NONE\" and select(5, GetSpecializationInfo(GetSpecialization())) or UnitGroupRolesAssigned(\"player\"),\n        [\"leader\"] = UnitIsGroupLeader(\"player\"),\n        [\"players\"] = config.playerMarks.tank > 1 or config.playerMarks.healer > 1,\n        [\"seasonScore\"] = C_PlayerInfo.GetPlayerMythicPlusRatingSummary(\"player\").currentSeasonScore,\n        [\"mobs\"] = aura_env.PlayerWantsToMark(),\n        [\"marker\"] = aura_env.PlayerWantsToMark(),\n    }\n}\n\naura_env.AddNpcToQueue = function(guid, npcId)\n    local prio = aura_env.mobs[npcId].prio\n\n    -- Create a new queue if it doesn't exist\n    aura_env.queue[guid] = aura_env.queue[guid] or {}\n\n    -- Loop through the prio table and handle markings\n    for _, mark in pairs(prio) do\n        if mark > 1 and mark < 10 then \n            local markId = 10 - mark\n\n            -- Add the marking to the queue if it doesn't already exist\n            if not aura_env.queue[guid].mark or not aura_env.queue[guid].mark[markId] then \n                aura_env.queue[guid].mark = aura_env.queue[guid].mark or {}\n                aura_env.queue[guid].mark[markId] = true\n            end\n        end\n    end\nend\n\naura_env.GetAvailableMarkFromMarkOrder = function(npcId)\n    -- Returns an INT with available MarkId or nil if none is found. \n    for i = 1, 8 do \n        local markOrder = config.markOptions[\"markOrder\"..i]\n        local markId = 9 - markOrder\n        local npc = aura_env.mobs[npcId]\n        local mark = aura_env.marks[markId]\n        \n        local isAvailable = (not mark.active or\n                            (mark.time and\n                            (npc.makeAvailable == 2 or \n                            npc.makeAvailable == 3) and\n                            (GetTime() > mark.time))) and\n                            config.markOptions[\"toggleMark\"..markId] and\n                            10 - config.playerMarks.tank ~= markId and\n                            10 - config.playerMarks.healer ~= markId\n\n        if isAvailable then \n            return markId\n        end\n    end\n    return nil\nend\n\naura_env.CheckIfMarkIsAvailable = function(markId, npcId)\n    -- Returns a BOOLEAN if the mark is available.\n    local markData = aura_env.marks[markId]\n    local mobData = aura_env.mobs[npcId]\n\n    local isAvailable = not markData.active or \n                        (markData.time and \n                        (mobData.makeAvailable == 2 or mobData.makeAvailable == 3) and \n                        (GetTime() > markData.time))\n\n    return isAvailable\nend\n\naura_env.UpdatePlayerMarks = function()\n    local playerName = aura_env.GetPlayerName()\n\n    if not playerName then return end -- Error checking\n    \n    -- Exit the function if the player is not marker\n    if not aura_env.partyMembers[playerName].marker then \n        return \n    end\n    \n    -- Loop through all members of the player's group\n    for i = 0, GetNumGroupMembers() do\n        -- Determine the unit ID of the current member\n        local unit = i == 0 and \"player\" or \"party\"..i\n        -- Get the role of the current member\n        local role = UnitGroupRolesAssigned(unit) == \"NONE\" and select(5, GetSpecializationInfo(GetSpecialization())) or UnitGroupRolesAssigned(unit)\n        if role == \"TANK\" and not (config.playerMarks.tank == 1) then \n            SetRaidTarget(unit, 10 - config.playerMarks.tank)\n        elseif role == \"HEALER\" and not (config.playerMarks.healer == 1) then \n            SetRaidTarget(unit, 10 - config.playerMarks.healer)\n        end\n    end\nend\n\n\naura_env.SendPlayerInfo = function()\n    -- Get player's role. Use specialization name if role is not assigned.\n    local role = UnitGroupRolesAssigned(\"player\") == \"NONE\" and select(5, GetSpecializationInfo(GetSpecialization())) or UnitGroupRolesAssigned(\"player\")\n\n    -- Check if player is the leader, and get their current mythic+ score\n    local isLeader = UnitIsGroupLeader(\"player\")\n    local seasonScore = C_PlayerInfo.GetPlayerMythicPlusRatingSummary(\"player\").currentSeasonScore\n\n    -- Determine if mobs should be marked based on role, leader status, and config settings\n    local shouldMarkMobs = config.markerInfo.role == 1 or\n                           (config.markerInfo.role == 2 and role == \"TANK\") or\n                           (config.markerInfo.role == 3 and isLeader) or\n                           (config.markerInfo.role == 4 and (isLeader or role == \"TANK\"))\n\n    -- Determine if players should be marked based on config settings\n    local shouldMarkPlayers = (config.playerMarks.tank > 1 or config.playerMarks.healer > 1)\n\n    -- Construct the message to send over addon chat\n    local message = string.format(\"%s:%s:%s:%s:%s\", role, tostring(shouldMarkMobs), tostring(shouldMarkPlayers), tostring(isLeader), tostring(seasonScore))\n\n    -- Send the message over addon chat\n    C_ChatInfo.SendAddonMessage(\"MYTHICAUTOMARK\", \"DECIDE_MARKER^\"..message, aura_env.GetChatType())\nend\n\naura_env.SendRoleCall = function()\n    -- Send message to others to update\n    C_ChatInfo.SendAddonMessage(\"MYTHICAUTOMARK\", \"UPDATE_PLAYER^nil\", aura_env.GetChatType())\nend\n\naura_env.ConvertAddonMsg = function(msg)\n    local t = {}\n    for v in string.gmatch(msg, \"[^:]+\") do\n        if v == \"true\" then\n            t[#t+1] = true\n        elseif v == \"false\" then\n            t[#t+1] = false\n        else\n            local num = tonumber(v)\n            t[#t+1] = num and num or v\n        end\n    end\n    return unpack(t)\nend\n\naura_env.ShouldAutoMarkNameplate = function(unit, automark, guid)\n    -- Returns a BOOLEAN if the criteras are met.\n    local shouldAutoMark = (automark == 3 or (automark == 4 and UnitAffectingCombat(unit) and not GetRaidTargetIndex(unit))) and UnitExists(unit)\n\n    return shouldAutoMark\nend\n\naura_env.ShouldAutoMarkMouseover = function(unit, automark)\n    -- Returns a BOOLEAN if the criteras is met.\n    local shouldAutoMark = not UnitIsDead(unit) and not UnitIsPlayer(unit) and not GetRaidTargetIndex(unit) and automark == 2\n\n    return shouldAutoMark\nend\n\naura_env.IsMobAlreadyMarked = function(guid)\n    -- Returns a BOOLEAN with information if the mob is marked or not. \n    for _, mark in pairs(aura_env.marks) do\n        if mark.guid == guid then\n            return true -- Mob is already marked\n        end\n    end\n    return false -- Mob is not already marked\nend\n\naura_env.ApplyAndAnnounceMark = function(unit, npcId, guid, mark, event)\n    --\n    local markData = aura_env.marks[mark]\n\n    if not markData then\n        return -- Invalid mark\n    end\n\n    -- Announce the mark so that players can pick it up. \n    local assignees = aura_env.mobs[npcId].assignee\n\n    if assignees then \n        for _, assignee in pairs(assignees) do\n            -- Announce\n            local announce = string.format(\"%d:%d:%d:%d:%d:%s:%s\", mark, assignee.id, assignee.type, assignee.role, assignee.class, guid, assignee.playerName)\n            C_ChatInfo.SendAddonMessage(\"MYTHICAUTOMARK\", \"ANNOUNCE^\"..announce, aura_env.GetChatType())\n        end\n    end\n    -- Apply\n    markData.guid = guid\n    markData.time = GetTime() + config.global.makeAvailableTime\n    markData.active = true\n    SetRaidTarget(unit, mark)\nend\n\naura_env.ResetMarksAndQueue = function()\n    for k in pairs(aura_env.marks) do \n        aura_env.marks[k].active = false\n        aura_env.marks[k].guid = nil\n    end\n    aura_env.queue = {}\nend\n\naura_env.FindAvailableMarkAndNameplate = function(mob, npcId)\n    for i = 1, 8 do\n        if mob.mark[i] and aura_env.CheckIfMarkIsAvailable(i, npcId) then\n            return i, mob.nameplate\n        end\n    end\n    return nil, nil\nend\n\naura_env.MarkNextInQueue = function()\n    local queue = aura_env.queue\n    local mob, marks = next(queue)\n\n    -- Nothing in Queue, return.\n    if not mob then\n        return\n    end\n\n    local nameplate = aura_env.GetUnitIdFromGUID(mob)\n    local npcId = aura_env.GetNpcIdFromGUID(mob)\n\n    if not nameplate then\n        queue[mob] = nil\n        aura_env.MarkNextInQueue()\n    end\n\n    local mark\n\n    if not marks then\n        -- Queue Mob doesn't have associated Marks\n        mark = aura_env.GetAvailableMarkFromMarkOrder(npcId)\n    else\n        -- Queue Mob has associated Marks, try to use them.\n        for id in pairs(marks) do\n            if aura_env.CheckIfMarkIsAvailable(id, npcId) then\n                mark = id\n                break\n            end\n        end\n    end\n\n    -- Apply and announce the mark if both nameplate and mark are available\n    if mark then\n        aura_env.ApplyAndAnnounceMark(nameplate, npcId, mob, mark)\n        queue[mob] = nil\n    end\nend\n\naura_env.ProcessUnitDeath = function(guid)\n    -- Get the NPC ID associated with the GUID\n    local npcId = aura_env.GetNpcIdFromGUID(guid)\n    \n    -- Get the mob data for the NPC\n    local mobData = aura_env.mobs[npcId]\n\n    -- Check if the dead unit was in our table; if not, return\n    if not mobData then\n        return\n    end\n\n    -- Remove the dead unit from the queue if it was present\n    aura_env.queue[guid] = nil\n\n    -- Check if the dead unit had an associated mark and deactivate it\n    for _, markData in pairs(aura_env.marks) do\n        if markData.guid == guid then\n            markData.active = false\n            break\n        end\n    end\n\n    -- Check if the next mob in the queue should be marked\n    local markNextInQueue = mobData.unitDied == 2\n\n    if markNextInQueue then\n        aura_env.MarkNextInQueue()\n    else\n        return\n    end\nend\n\n\naura_env.ShouldChangeMarker = function(senderRole, senderMarkMobs, senderMarkPlayers, senderLeader, senderSeasonScore)\n    local currentMarkerData\n    \n    -- Search for current marker\n    for _, memberData in pairs(aura_env.partyMembers) do\n        if memberData.marker then\n            currentMarkerData = memberData\n            break\n        end\n    end\n\n    -- Couldn't find any marker, return\n    if not currentMarkerData then \n        return \n    end\n\n    local currentMarkerRole = currentMarkerData.role\n    local currentMarkerLeader = currentMarkerData.leader\n    local currentMarkerSeasonScore = currentMarkerData.seasonScore\n\n    if senderRole == \"TANK\" and currentMarkerRole ~= senderRole then \n        return true\n    elseif senderLeader and not currentMarkerLeader and currentMarkerRole ~= \"TANK\" then \n        return true\n    elseif senderRole == \"DAMAGER\" and currentMarkerRole ~= \"TANK\" and (currentMarkerRole ~= \"DAMAGER\" or senderSeasonScore > currentMarkerSeasonScore) then \n        return true\n    elseif senderRole == \"HEALER\" and currentMarkerRole ~= \"TANK\" and currentMarkerRole ~= \"DAMAGER\" and (currentMarkerRole ~= \"HEALER\" or senderSeasonScore > currentMarkerSeasonScore) then \n        return true\n    end\n\n    return false\nend\n\naura_env.UpdatePartyMember = function(sender, role, markMobs, markPlayers, leader, seasonScore)\n    if not aura_env.partyMembers[sender] then\n        aura_env.partyMembers[sender] = {}\n    end\n\n    local member = aura_env.partyMembers[sender]\n\n    member.role = role\n    member.mobs = markMobs\n    member.players = markPlayers\n    member.leader = leader\n    member.seasonScore = seasonScore\nend\n\naura_env.UpdatePartyMemberMarkers = function(exceptPlayer)\n    local currentMarkerData\n    \n    -- Search for current marker\n    for _, memberData in pairs(aura_env.partyMembers) do\n        if memberData.marker then\n            currentMarkerData = memberData\n            break\n        end\n    end\n    \n    if currentMarkerData then\n        for playerName, playerData in pairs(aura_env.partyMembers) do\n            if playerName == exceptPlayer then\n                playerData.marker = true\n            else\n                playerData.marker = false\n            end\n        end\n    end\nend\n\naura_env.RemoveOldPartyMembers = function()\n    for player in pairs(aura_env.partyMembers) do\n        local member = false\n        for unit in WA_IterateGroupMembers() do\n            local name = unit == \"player\" and string.format(\"%s-%s\", UnitFullName(unit)) or string.format(\"%s-%s\", UnitName(unit), GetNormalizedRealmName(unit))\n            if player == name then\n                member = true\n                break\n            end\n        end\n        if not member then aura_env.partyMembers[player] = nil end\n    end\nend",
