["custom"] = "function(event, prefix, text, channel, sender)\n    if event == \"CHAT_MSG_ADDON\" and prefix == \"MYTHICAUTOMARK\" then \n        local subEvent, msg = strsplit(\"^\", text)\n        if subEvent == \"UPDATE_PLAYER\" then \n            aura_env.SendPlayerInfo()\n        elseif subEvent == \"DECIDE_MARKER\" then \n            local senderRole, senderMarkMobs, senderMarkPlayers, senderLeader, senderSeasonScore = aura_env.ConvertAddonMsg(msg)\n            \n            -- If this is the first time we're seeing this player, update member data\n            if not aura_env.partyMembers[sender] then\n                aura_env.UpdatePartyMember(sender, senderRole, senderMarkMobs, senderMarkPlayers, senderLeader, senderSeasonScore)\n            end\n\n            local senderData = aura_env.partyMembers[sender]\n\n            -- Check if any member data has changed\n            if senderData.role ~= senderRole or \n                senderData.leader ~= senderLeader or \n                senderData.mobs ~= senderMarkMobs or \n                senderData.players ~= senderMarkPlayers or\n                senderData.seasonScore ~= senderSeasonScore then \n                aura_env.UpdatePartyMember(sender, senderRole, senderMarkMobs, senderMarkPlayers, senderLeader, senderSeasonScore)\n                -- Send member data if it's not the local player\n                if sender ~= aura_env.GetPlayerName() then \n                    aura_env.SendPlayerInfo() \n                end \n            end\n            \n            -- Check if a marker change is needed\n            if aura_env.ShouldChangeMarker(senderRole, senderMarkMobs, senderMarkPlayers, senderLeader, senderSeasonScore) then \n                aura_env.UpdatePartyMemberMarkers(sender)\n                return true\n            end\n        end\n    end\nend\n\n-- CHAT_MSG_ADDON",
