["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        for i = 1, aura_env.config.myTurnOnly and 1 or 2 do\n            local spellID = i == 1 and 119381 or 179057 -- Leg Sweep/Chaos Nova\n            local spellName, _, icon = GetSpellInfo(spellID)\n            \n            allstates[i] = {\n                show = true,\n                changed = true,\n                unit = \"player\",\n                spellName = spellName,\n                next = i > 1,\n                index = i,\n                icon = icon\n            }\n        end\n    elseif event == \"LIQUID_ASSIGNMENT_EVENT\" then\n        local id = ...\n        \n        if id ~= aura_env.id then return end\n        if not aura_env.affectedCounts then return end\n        \n        -- Hide states, then show them again as necessary\n        for _, state in pairs(allstates) do\n            state.show = false\n            state.changed = true\n        end\n        \n        local currentTime = GetTime()\n        local inCombat = InCombatLockdown()\n        local currentlyAssignedState = allstates[1]\n        \n        -- Sort the CCs based on availability and priority (as defined in init)\n        -- CCs of players that are dead/offline/out of range are sorted last\n        table.sort(\n            aura_env.cooldownQueue,\n            function(dataA, dataB)\n                local unitA = aura_env.GUIDToUnit[dataA.GUID]\n                local unitB = aura_env.GUIDToUnit[dataB.GUID]\n                \n                local deadA = UnitIsDeadOrGhost(unitA)\n                local deadB = UnitIsDeadOrGhost(unitB)\n                \n                local connectedA = UnitIsConnected(unitA)\n                local connectedB = UnitIsConnected(unitB)\n                \n                local inRangeA = UnitInRange(unitA)\n                local inRangeB = UnitInRange(unitB)\n                \n                local readyA = dataA.charges >= 1 or dataA.expirationTime <= currentTime\n                local readyB = dataB.charges >= 1 or dataB.expirationTime <= currentTime\n                \n                local currentlyAssignedA = currentlyAssignedState and dataA.GUID == currentlyAssignedState.GUID and dataA.spellID == currentlyAssignedState.spellID\n                local currentlyAssignedB = currentlyAssignedState and dataB.GUID == currentlyAssignedState.GUID and dataB.spellID == currentlyAssignedState.spellID\n                \n                local affectedCountA = aura_env.affectedCounts[dataA.type]\n                local affectedCountB = aura_env.affectedCounts[dataB.type]\n                \n                if deadA ~= deadB then\n                    return deadB\n                elseif connectedA ~= connectedB then\n                    return connectedA\n                elseif inRangeA ~= inRangeB then\n                    return inRangeA\n                elseif readyA ~= readyB then\n                    return readyA\n                elseif readyA then\n                    -- If both spells are ready, sort by number of monsters that are affected by their CC types (highest first)\n                    if affectedCountA ~= affectedCountB then\n                        return affectedCountA > affectedCountB\n                    elseif inCombat and currentlyAssignedA ~= currentlyAssignedB then\n                        -- While in combat, we prefer to not swap assignments unless we have to\n                        -- If there are no spells available with fewer immunes than the currently assigned one, always keep it\n                        return currentlyAssignedA\n                    elseif dataA.priority ~= dataB.priority then\n                        return dataA.priority < dataB.priority\n                    else\n                        return dataA.GUID < dataB.GUID\n                    end\n                else\n                    -- If neither spell is ready, sort by expirationTime\n                    if dataA.expirationTime ~= dataB.expirationTime then\n                        return dataA.expirationTime < dataB.expirationTime\n                    else\n                        return dataA.GUID < dataB.GUID\n                    end\n                end\n            end\n        )\n        \n        if not inCombat then return true end -- Only show the aura while in combat\n        \n        -- Show the next two assignments\n        -- If the \"show only on my turn\" option is enabled, we only ever show the current assignment, not the one after\n        for i = 1, aura_env.config.myTurnOnly and 1 or 2 do\n            local cooldownData = aura_env.cooldownQueue[i]\n            \n            if cooldownData and (not aura_env.config.myTurnOnly or cooldownData.GUID == WeakAuras.myGUID) then\n                local affectedCount = aura_env.affectedCounts[cooldownData.type]\n                \n                -- Only show CCs that at least one monster is affected by\n                if affectedCount > 0 then\n                    local isReady = cooldownData.charges >= 1 or cooldownData.expirationTime <= currentTime\n                    local GUID = cooldownData.GUID\n                    local unit = aura_env.GUIDToUnit[GUID]\n                    local spellName, _, icon = GetSpellInfo(cooldownData.spellID)\n                    \n                    allstates[i] = {\n                        show = true,\n                        changed = true,\n                        progressType = \"timed\",\n                        duration = cooldownData.duration,\n                        expirationTime = isReady and currentTime or cooldownData.expirationTime,\n                        GUID = GUID,\n                        unit = unit,\n                        isPlayer = UnitIsUnit(unit, \"player\"),\n                        spellID = cooldownData.spellID,\n                        spellName = spellName,\n                        type = cooldownData.type,\n                        icon = icon,\n                        next = i > 1,\n                        index = i,\n                        autoHide = false\n                    }\n                end\n            end\n        end\n        \n        -- Play TTS if appropriate (enabled in custom options, our turn, was not our turn before)\n        if aura_env.config.tts then\n            local nextAssignment = allstates[1]\n            \n            if not (nextAssignment and nextAssignment.show) then return true end\n            \n            local isMyAssignment = nextAssignment.GUID == WeakAuras.myGUID\n            local playerChanged = nextAssignment.GUID ~= aura_env.lastAssignment.GUID\n            local spellChanged = nextAssignment.spellID ~= aura_env.lastAssignment.spellID\n            \n            if isMyAssignment and (playerChanged or spellChanged) then\n                C_VoiceChat.SpeakText(\n                    aura_env.config.ttsVoice,\n                    \"Next\",\n                    Enum.VoiceTtsDestination.LocalPlayback,\n                    C_TTSSettings and C_TTSSettings.GetSpeechRate() or 0,\n                    aura_env.config.ttsVolume\n                )\n            end\n            \n            aura_env.lastAssignment.GUID = nextAssignment.GUID\n            aura_env.lastAssignment.spellID = nextAssignment.spellID\n        end\n        \n        return true\n    elseif event == \"TRIGGER\" then\n        local _, states = ...\n        \n        if not aura_env.npcToEffectiveCC then return end\n        \n        -- Used to compare against for updates\n        local oldAffectedCounts = aura_env.affectedCounts and CopyTable(aura_env.affectedCounts) or {}\n        \n        -- Number of relevant mobs in the current pull that are affected by each CC type\n        aura_env.affectedCounts = {\n            DISPLACEMENT = 0,\n            DISORIENT = 0,\n            STUN = 0,\n            SILENCE = 0,\n            FEAR = 0,\n            INCAPACITATE = 0\n        }\n        \n        -- For every nameplate we are in combat with, check if they require AoE stops (as configured in custom options)\n        -- If they do, check which CC types they are affected by\n        for _, state in pairs(states) do\n            local npcID = tonumber(state.npcId)\n            local effectiveCC = npcID and aura_env.npcToEffectiveCC[npcID] -- Types of CC that this NPC is affected by\n            \n            -- Entry may not have been found because it's keyed by name rather than npc ID\n            if not effectiveCC then\n                local name = state.name\n                \n                effectiveCC = name and aura_env.npcToEffectiveCC[name]\n            end\n            \n            -- Increment CC types this monster is affected by\n            if effectiveCC then\n                for ccType in pairs(effectiveCC) do\n                    aura_env.affectedCounts[ccType] = aura_env.affectedCounts[ccType] + 1\n                end\n            end\n        end\n        \n        -- If the number of mobs affected by any of the CC types changed, update\n        for ccType, count in pairs(oldAffectedCounts) do\n            if count ~= aura_env.affectedCounts[ccType] then\n                WeakAuras.ScanEvents(\"LIQUID_ASSIGNMENT_EVENT\", aura_env.id)\n                \n                return\n            end\n        end\n    elseif event == \"LIQUID_CooldownListUpdate\" then\n        local id, unit, unitCooldowns = ...\n        \n        if id ~= aura_env.id then return end\n        \n        local changed = false\n        \n        if unitCooldowns then\n            for spellID, cooldownInfo in pairs(unitCooldowns) do\n                if aura_env.UpdateEntry(unit, spellID, cooldownInfo) then\n                    changed = true\n                end\n            end\n        end\n        \n        if changed then\n            WeakAuras.ScanEvents(\"LIQUID_ASSIGNMENT_EVENT\", aura_env.id)\n        end\n    elseif event == \"LIQUID_CooldownListWipe\" then\n        local id = ...\n        \n        if id ~= aura_env.id then return end\n        \n        aura_env.RebuildQueue()\n    elseif event == \"LIQUID_CooldownUpdate\" then\n        local id, unit, spellID, cooldownInfo = ...\n        \n        if id ~= aura_env.id then return end\n        \n        if aura_env.UpdateEntry(unit, spellID, cooldownInfo) then\n            WeakAuras.ScanEvents(\"LIQUID_ASSIGNMENT_EVENT\", aura_env.id)\n        end\n    elseif event == \"LIQUID_CooldownAdded\" then\n        local id, unit, spellID, cooldownInfo = ...\n        \n        if id ~= aura_env.id then return end\n        \n        if aura_env.UpdateEntry(unit, spellID, cooldownInfo) then\n            WeakAuras.ScanEvents(\"LIQUID_ASSIGNMENT_EVENT\", aura_env.id)\n        end\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent, _, _, _, _, _, destGUID = ...\n        \n        if subEvent == \"UNIT_DIED\" and aura_env.GUIDToUnit[destGUID] then\n            WeakAuras.ScanEvents(\"LIQUID_ASSIGNMENT_EVENT\", aura_env.id)\n        end\n    elseif event == \"GROUP_ROSTER_UPDATE\" then\n        local changed = false\n        \n        aura_env.GUIDToUnit = {}\n        \n        for unit in WA_IterateGroupMembers() do\n            aura_env.GUIDToUnit[UnitGUID(unit)] = unit\n        end\n        \n        for i, cooldownData in ipairs_reverse(aura_env.cooldownQueue) do\n            if not aura_env.GUIDToUnit[cooldownData.GUID] then\n                table.remove(aura_env.cooldownQueue, i)\n                \n                changed = true\n            end\n        end\n        \n        if changed then\n            WeakAuras.ScanEvents(\"LIQUID_ASSIGNMENT_EVENT\", aura_env.id)\n        end\n    elseif event == \"PLAYER_REGEN_ENABLED\" then\n        aura_env.lastAssignment = {} -- When we leave combat, make sure we play TTS again the next time we enter combat and it's our turn\n        \n        WeakAuras.ScanEvents(\"LIQUID_ASSIGNMENT_EVENT\", aura_env.id)\n    elseif event == \"PLAYER_REGEN_DISABLED\" then\n        WeakAuras.ScanEvents(\"LIQUID_ASSIGNMENT_EVENT\", aura_env.id)\n    elseif event == \"CHALLENGE_MODE_START\" or event == \"STATUS\" then\n        aura_env.lastAssignment = {} -- Last GUID/spell to be assigned. Used to track if we should play TTS.\n        aura_env.GUIDToUnit = {}\n        aura_env.npcToEffectiveCC = {} -- Mapping of npc ID/name to CC types that they are affected by\n        \n        for unit in WA_IterateGroupMembers() do\n            aura_env.GUIDToUnit[UnitGUID(unit)] = unit\n        end\n        \n        -- If we are currently in an active mythic+ dungeon, use the custom options settings\n        -- If not, we assume all monsters are affected by all CC types\n        local mapID = C_ChallengeMode.GetActiveChallengeMapID()\n        \n        if mapID then\n            local ccTypes = {\n                \"DISPLACEMENT\",\n                \"DISORIENT\",\n                \"STUN\",\n                \"SILENCE\",\n                \"FEAR\",\n                \"INCAPACITATE\"\n            }\n            \n            local infoTable = aura_env.config[tostring(mapID)] -- Contains info about monsters in this dungeon, and the CC types they are affected by\n            \n            if infoTable then\n                -- Populate the effective CC types table\n                for _, info in ipairs(infoTable) do\n                    local key =  info.npcID > 0 and info.npcID or info.name\n                    local effectiveCC = {}\n                    \n                    for i, isAffected in ipairs(info.types) do\n                        local ccType = ccTypes[i]\n                        \n                        if isAffected then\n                            effectiveCC[ccType] = true\n                        end\n                    end\n                    \n                    aura_env.npcToEffectiveCC[key] = effectiveCC\n                end\n            end\n        end\n        \n        aura_env.BroadcastProfile()\n        aura_env.BroadcastVersion()\n        aura_env.RebuildQueue()\n    end\nend",
