["customText"] = "function()\n    if WeakAuras.IsOptionsOpen() then\n        local name = UnitName(\"player\")\n        local class = UnitClassBase(\"player\")\n        local colorStr = RAID_CLASS_COLORS[class].colorStr\n        \n        return string.format(\"|c%s%s|r\", colorStr, name)\n    end\n    \n    local name = UnitName(aura_env.state.unit)\n    \n    \n    if IsInGroup(\"player\") or IsInRaid(\"player\") then\n        \n        local classFileName = UnitClassBase(aura_env.state.unit)\n        local colorStr = RAID_CLASS_COLORS[classFileName].colorStr\n        \n        return string.format(\"|c%s%s|r\", colorStr, name)\n    else\n        return name\n    end\nend\n\n\n",
