["custom"] = "local lib = LibStub:GetLibrary(\"LibOpenRaid-1.0\")\n\n\n-- Define the tracked cooldowns field\nlocal defaultTrackedCooldowns = aura_env.config.TrackedCooldowns or {}\n\n\nlocal trackedCooldowns = {}\n\nfor key, value in pairs(defaultTrackedCooldowns) do\n    local currentSpellID = string.gsub(key, \"_order\", \"\")\n    \n    if not trackedCooldowns[currentSpellID] then\n        \n        -- Initialize the entry\n        trackedCooldowns[currentSpellID] = {\n            enabled = false,\n            order = 99,\n            spellID = currentSpellID\n        }\n        \n    end\n    \n    \n    -- Update the enabled field if the value is a boolean\n    if type(value) == \"boolean\" and value == true then\n        trackedCooldowns[currentSpellID].enabled = value\n    end\n    \n    -- Update the order field if it's not related to \"order\" and the value is a number\n    if type(value) == \"number\" then\n        trackedCooldowns[currentSpellID].order = value\n    end\nend\n\n\nlocal sortedTrackedCooldowns = {}\nfor _, entry in pairs(trackedCooldowns) do\n    if entry.enabled == true then\n        table.insert(sortedTrackedCooldowns, entry)\n    end\n    \nend\n\n\n-- Tracked cooldowns, and the order that they should be used in\ntrackedCooldowns = {}\n\nfor _, entry in pairs(sortedTrackedCooldowns) do\n    local spellID = tonumber(entry.spellID)\n    local priority = tonumber(entry.order)\n    trackedCooldowns[spellID] = priority    \nend\n\n\n\n\n\n\n-- Register LibOpenRaid callbacks\nif not aura_env.region.callbacksRegistered then\n    local id = aura_env.id\n    \n    local callbacks = {\n        CooldownListUpdate = function(...) WeakAuras.ScanEvents(\"CooldownListUpdate\", id, ...) end,\n        CooldownListWipe = function(...) WeakAuras.ScanEvents(\"CooldownListWipe\", id, ...) end,\n        CooldownUpdate = function(...) WeakAuras.ScanEvents(\"CooldownUpdate\", id, ...) end,\n        CooldownAdded = function(...) WeakAuras.ScanEvents(\"CooldownAdded\", id, ...) end\n    }\n    \n    lib.RegisterCallback(callbacks, \"CooldownListUpdate\", \"CooldownListUpdate\")\n    lib.RegisterCallback(callbacks, \"CooldownListWipe\", \"CooldownListWipe\")\n    lib.RegisterCallback(callbacks, \"CooldownUpdate\", \"CooldownUpdate\")\n    lib.RegisterCallback(callbacks, \"CooldownAdded\", \"CooldownAdded\")\n    \n    aura_env.region.callbacksRegistered = true\nend\n\n-- Update an entry in the queue, or create it if it did not exist yet\nfunction aura_env.UpdateEntry(unit, spellID, cooldownInfo)\n    if not unit then return end\n    if not UnitExists(unit) then return end\n    if not spellID then return end\n    if not trackedCooldowns[spellID] then return end\n    if not cooldownInfo then return end\n    if not aura_env.cooldownQueue then return end\n    \n    local GUID = UnitGUID(unit)\n    local _, _, timeLeft, charges, _, _, _, duration = lib.GetCooldownStatusFromCooldownInfo(cooldownInfo)\n    \n    -- Try to update existing entry\n    for _, cooldownData in ipairs(aura_env.cooldownQueue) do\n        if cooldownData.GUID == GUID and cooldownData.spellID == spellID then\n            cooldownData.expirationTime = timeLeft + GetTime()\n            cooldownData.duration = duration\n            cooldownData.charges = charges\n            \n            return true\n        end\n    end\n    \n    -- Create new entry\n    table.insert(\n        aura_env.cooldownQueue,\n        {\n            GUID = GUID,\n            spellID = spellID,\n            priority = trackedCooldowns[spellID],\n            expirationTime = timeLeft + GetTime(),\n            duration = duration,\n            charges = charges\n        }\n    )\n    \n    return true\nend\n\n-- Rebuild the cooldown queue and request cooldown information for each entry\nfunction aura_env.RebuildQueue()\n    if aura_env.lastRebuild and aura_env.lastRebuild == GetTime() then return end\n    \n    aura_env.cooldownQueue = {}\n    \n    local allUnitsCooldown = lib.GetAllUnitsCooldown()\n    \n    if allUnitsCooldown then\n        for unit, unitCooldowns in pairs(allUnitsCooldown) do\n            for spellID, cooldownInfo in pairs(unitCooldowns) do\n                if trackedCooldowns[spellID] then\n                    aura_env.UpdateEntry(unit, spellID, cooldownInfo)\n                end\n            end\n        end\n    end\n    \n    WeakAuras.ScanEvents(\"ASSIGNMENT_EVENT\", aura_env.id)\nend",
["do_custom"] = true,
},
