["custom"] = "function(allstates, event, ...)\n    \n    local triggerNum = ...\n    \n    -- Define the tracked cooldowns field\n    local defaultTrackedCooldowns = aura_env.config.TrackedCooldowns or {}\n    \n    \n    local trackedCooldowns = {}\n    \n    for key, value in pairs(defaultTrackedCooldowns) do\n        local currentSpellID = string.gsub(key, \"_order\", \"\")\n        \n        if not trackedCooldowns[currentSpellID] then\n            \n            -- Initialize the entry\n            trackedCooldowns[currentSpellID] = {\n                enabled = false,\n                order = 99,\n                spellID = currentSpellID\n            }\n            \n        end\n        \n        -- Update the enabled field if the value is a boolean\n        if type(value) == \"boolean\" then\n            trackedCooldowns[currentSpellID].enabled = value\n        end\n        \n        -- Update the order field if it's not related to \"order\" and the value is a number\n        if type(value) == \"number\" then\n            trackedCooldowns[currentSpellID].order = value\n        end\n        \n    end\n    \n    \n    local sortedTrackedCooldowns = {}\n    for _, entry in pairs(trackedCooldowns) do\n        if entry.enabled == true then\n            table.insert(sortedTrackedCooldowns, entry)\n        end\n    end\n    table.sort(sortedTrackedCooldowns, function(a, b)\n            return a.order < b.order\n    end)\n    \n    \n    if event == \"OPTIONS\" then\n        local spellName, _, icon = GetSpellInfo(sortedTrackedCooldowns[1].spellID)\n        allstates[1] = {\n            show = true,\n            changed = true,\n            unit = \"player\",\n            spellName = spellName,\n            next = false,\n            index = 1,\n            icon = icon\n        }\n        \n        spellName, _, icon = GetSpellInfo(sortedTrackedCooldowns[2].spellID)\n        \n        allstates[2] = {\n            show = true,\n            changed = true,\n            unit = \"player\",\n            spellName = spellName,\n            next = true,\n            index = 2,\n            icon = icon\n        }\n        \n    elseif event == \"TRIGGER\" and triggerNum == 2 then\n        local id, override = ...\n        \n        if id ~= aura_env.id then return end\n        \n        WeakAuras.ScanEvents(\"ASSIGNMENT_EVENT\", aura_env.id, true)\n        \n    elseif event == \"ASSIGNMENT_EVENT\" then\n        \n        local id, override = ...\n        \n        if id ~= aura_env.id then return end\n        \n        local currentTime = GetTime()\n        \n        table.sort(\n            aura_env.cooldownQueue,\n            function(dataA, dataB)\n                local unitA = aura_env.GUIDToUnit[dataA.GUID]\n                local unitB = aura_env.GUIDToUnit[dataB.GUID]\n                \n                local deadA = UnitIsDeadOrGhost(unitA)\n                local deadB = UnitIsDeadOrGhost(unitB)\n                \n                local inRangeA = UnitInRange(unitA)\n                local inRangeB = UnitInRange(unitB)\n                \n                local readyA = dataA.charges >= 1 or dataA.expirationTime <= currentTime\n                local readyB = dataB.charges >= 1 or dataB.expirationTime <= currentTime\n                \n                if deadA ~= deadB then\n                    return deadB\n                elseif inRangeA ~= inRangeB then\n                    return inRangeA\n                elseif readyA ~= readyB then\n                    return readyA\n                elseif readyA then\n                    -- If both spells are ready, sort by priority\n                    if dataA.priority ~= dataB.priority then\n                        return dataA.priority < dataB.priority\n                    else\n                        return dataA.GUID < dataB.GUID\n                    end\n                else\n                    -- If neither spell is ready, sort by expirationTime\n                    if dataA.expirationTime ~= dataB.expirationTime then\n                        return dataA.expirationTime < dataB.expirationTime\n                    else\n                        return dataA.GUID < dataB.GUID\n                    end\n                end\n            end\n        )\n        \n        -- First check if the currently shown ability is still available\n        -- If it is, don't override it in combat\n        local available = false\n        local state = allstates[1]\n        \n        for s = 1 , 2, 1 do\n            state = allstates[s]\n            if state then\n                for _, cooldownData in ipairs(aura_env.cooldownQueue) do\n                    if cooldownData.GUID == state.GUID and cooldownData.spellID == state.spellID then\n                        local unit = aura_env.GUIDToUnit[cooldownData.GUID]\n                        local dead = UnitIsDeadOrGhost(unit)\n                        local isReady = cooldownData.charges >= 1 or cooldownData.expirationTime <= currentTime\n                        \n                    end\n                end\n            end\n            available = isReady and not dead\n            state = allstates[1]\n            break\n        end\n        \n        \n        if available and not override then return end\n        \n        -- If the currently shown spell is no longer available, or we are not in combat, override it\n        local cooldownData = aura_env.cooldownQueue[1]\n        \n        if not cooldownData then\n            if state then\n                state.show = false\n                state.changed = true\n            end\n            \n            return true\n        end\n        \n        local isReady = cooldownData.charges >= 1 or cooldownData.expirationTime <= currentTime\n        local GUID = cooldownData.GUID\n        local unit = aura_env.GUIDToUnit[GUID]\n        local spellName, _, icon = GetSpellInfo(cooldownData.spellID)\n        \n        allstates[1] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = cooldownData.duration,\n            expirationTime = isReady and currentTime or cooldownData.expirationTime,\n            GUID = GUID,\n            unit = unit,\n            isPlayer = UnitIsUnit(unit, \"player\"),\n            spellID = cooldownData.spellID,\n            spellName = spellName,\n            icon = icon,\n            next = false,\n            index = 1,\n            autoHide = false,\n        }\n        \n        -- Show the second ability in the queue\n        cooldownData = aura_env.cooldownQueue[2]\n        \n        if not cooldownData then\n            if state then\n                state.show = false\n                state.changed = true\n            end\n            \n            return true\n        end\n        \n        isReady = cooldownData.charges >= 1 or cooldownData.expirationTime <= currentTime\n        GUID = cooldownData.GUID\n        unit = aura_env.GUIDToUnit[GUID]\n        spellName, _, icon = GetSpellInfo(cooldownData.spellID)\n        \n        allstates[2] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = cooldownData.duration,\n            expirationTime = isReady and currentTime or cooldownData.expirationTime,\n            GUID = GUID,\n            unit = unit,\n            isPlayer = UnitIsUnit(unit, \"player\"),\n            spellID = cooldownData.spellID,\n            spellName = spellName,\n            icon = icon,\n            next = true,\n            index = 2,\n            autoHide = false,\n        }\n        \n        return true\n    elseif event == \"CooldownListUpdate\" then\n        local id, unit, unitCooldowns = ...\n        \n        if id ~= aura_env.id then return end\n        \n        local changed = false\n        \n        if unitCooldowns then\n            for spellID, cooldownInfo in pairs(unitCooldowns) do\n                if aura_env.UpdateEntry(unit, spellID, cooldownInfo) then\n                    changed = true\n                end\n            end\n        end\n        \n        if changed then\n            WeakAuras.ScanEvents(\"ASSIGNMENT_EVENT\", aura_env.id)\n        end\n    elseif event == \"CooldownListWipe\" then\n        local id = ...\n        \n        if id ~= aura_env.id then return end\n        \n        aura_env.RebuildQueue()\n    elseif event == \"CooldownUpdate\" then\n        local id, unit, spellID, cooldownInfo = ...\n        \n        if id ~= aura_env.id then return end\n        \n        if aura_env.UpdateEntry(unit, spellID, cooldownInfo) then\n            WeakAuras.ScanEvents(\"ASSIGNMENT_EVENT\", aura_env.id)\n        end\n    elseif event == \"CooldownAdded\" then\n        local id, unit, spellID, cooldownInfo = ...\n        \n        if id ~= aura_env.id then return end\n        \n        if aura_env.UpdateEntry(unit, spellID, cooldownInfo) then\n            WeakAuras.ScanEvents(\"ASSIGNMENT_EVENT\", aura_env.id)\n        end\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent, _, _, _, _, _, destGUID = ...\n        \n        if subEvent == \"UNIT_DIED\" and aura_env.GUIDToUnit[destGUID] then\n            WeakAuras.ScanEvents(\"ASSIGNMENT_EVENT\", aura_env.id)\n        end\n    elseif event == \"GROUP_ROSTER_UPDATE\" then\n        local changed = false\n        \n        aura_env.GUIDToUnit = {}\n        \n        for unit in WA_IterateGroupMembers() do\n            aura_env.GUIDToUnit[UnitGUID(unit)] = unit\n        end\n        \n        for i, cooldownData in ipairs_reverse(aura_env.cooldownQueue) do\n            if not aura_env.GUIDToUnit[cooldownData.GUID] then\n                table.remove(aura_env.cooldownQueue, i)\n                \n                changed = true\n            end\n        end\n        \n        if changed then\n            WeakAuras.ScanEvents(\"ASSIGNMENT_EVENT\", aura_env.id)\n        end\n    elseif event == \"PLAYER_REGEN_ENABLED\" then\n        WeakAuras.ScanEvents(\"ASSIGNMENT_EVENT\", aura_env.id, true)\n    elseif event == \"PLAYER_REGEN_DISABLED\" then\n        WeakAuras.ScanEvents(\"ASSIGNMENT_EVENT\", aura_env.id, true)\n    elseif event == \"CHALLENGE_MODE_START\" or event == \"STATUS\" then\n        aura_env.GUIDToUnit = {}\n        \n        for unit in WA_IterateGroupMembers() do\n            aura_env.GUIDToUnit[UnitGUID(unit)] = unit\n        end\n        \n        aura_env.RebuildQueue()\n        \n        \n    end \nend",
